import React from 'react'
import { Consumer } from './Context'

class Todo extends React.Component {

    style = () => {
        console.log('Style called')
        const { complete } = this.props.todo
        console.log(this.props.todo)
        return { textDecoration: complete ? 'line-through': 'none' }
    }

    toggle = (id, dispatch, complete) => {
        console.log('toggle clicked')
        if(complete === true) {
            dispatch({
                type: "TOGGLE",
                payload: id
            })
        } else {
            dispatch({
                type: "TOGGLE_2",
                payload: id
            })
        }
    }



    render() {
        const { title, id , complete} = this.props.todo

        return (
        <Consumer>{value => {

            const { dispatch } = value

            return <h3 className='text-center bg-light border-bottom' style={this.style()}>
                        <i className='far fa-times-circle fa-sm float-left m-1 text-danger ' ></i>{title}
                        <input type='checkbox' className='m-2 float-right' onChange={this.toggle.bind(this, id, dispatch , complete)}/>
                </h3>

        }}</Consumer>

        )
    }
}

export default Todo
