import Axios from 'axios'
import React from 'react'
import { Consumer } from './Context'


const Todo = (props) => {


    const style = () => {

        const { complete } = props.todo

        return { textDecoration: complete ? 'line-through': 'none' }
    }

    const toggle = (id, dispatch, complete) => {

        if(complete === true) {
            dispatch({
                type: "TOGGLE",
                payload: id
            })
        } else {
            dispatch({
                type: "TOGGLE_2",
                payload: id
            })
        }
    }

    const remove = (id, dispatch) => {
        Axios.delete(`/todos/${id}`)
            .then(() => dispatch({
                type: "REMOVE",
                payload: id
            }))

        Axios.get('/todos')

    }




